//SPDX-License-Identifier: GPL-3.0
pragma solidity >= 0.8.0 <= 0.8.3;


  contract Auction{

    address public highestBidder; 
    uint public highestBid; 
    mapping(address => uint) public userBalances; 
    address owner;


    // contractor 
    // 1. Initialize highest bid and the bidder's address
    constructor() {
        highestBidder = msg.sender;
        highestBid = 0;
        owner = msg.sender;
    }

    // Function to process bid
    // 1. Check if the bid is greater than the current highest bid26
    // 2. Update status variable and the amount to return
    function bid() public payable {
        require(msg.value > highestBid,"There already is a higher bid.");
        userBalances[highestBidder] = highestBid;
        highestBidder = msg.sender;
        highestBid = msg.value;
    }


     function withdraw() public {
        address payable msgSender = payable(msg.sender);
        require( amountCheck(msgSender),"The amount to withdraw should be greater than zero");
        require( msgSender.send(userBalances[msgSender]), "Cannot sent");
        userBalances[msgSender] = 0;
    }

    function amountCheck(address _addr) public view returns(bool){
        if(userBalances[_addr] > 0)
            return true;
        return false;
    } 
    function getContractBalance() public view returns(uint){

        return(address(this).balance);
    }
}
